export default class Role extends Component {

  constructor(props) {
    super(props);
    this.state = {
      mylist:[],
      user:{
        username: "tester",
        phone: "0000000000",
        email: "email@email.com",
        address: "123 Sesame Street",
        emergencyContact: [
          {
            name: "John Doe", 
            phone: "1111111111"
          },
          {
            name: "First M. Last",
            phone: "0123456789"
          }
        ]
      },
      test:{
        username: "tester",
        phone: "0000000000"

      },
      errorMessage:""
    }
    // this.buttonClick = this.buttonClick.bind(this)
  }

  componentDidMount = () => {
    fetch("/user/user/get" + this.state.username)
        .then(response => response.json())
        .then(jsonData => {
            this.setState({
                phone: jsonData[0]["phone"],
                email: jsonData[0]["email"],
                address: jsonData[0]["address"],
                emergencyContact: jsonData[0]["emergencyContact"]
            })
        })
        .catch((error) => {
            console.error(error);
        })
  }



  saveUsers = async () => {
    console.log(this.state.test);
    const params = { id: 'admin', name: '用户' }

    const mylist = storageUtils.getList();
    console.log("mylist=>" ,mylist);
    this.setState({mylist});
    console.log("this.state.mylist=>" ,this.state.mylist)
    const result = await reqContact("abc","mytest1", "mytest2");
    if (true) {
      const  users  = result;
      console.log(result)
      console.log(users)
      // this.initRoles(roles);
      this.setState({ users});
    } else {
      // message.error("failsed");
    }
  };

  render() {

    return (
      <div>
        <div>
          <h3>Contact</h3>
          <input type = 'text' placeholder='phone' value = {this.state.user.phone} onChange={(e) => {}}/><br/>
          <input type = 'text' placeholder='email' value = {this.state.user.email} onChange={(e) => {}}/><br/>
          <input type = 'text' placeholder='address' value = {this.state.user.address} onChange={(e) => {}}/><br/>
        </div>
        <div>
          {this.state.user.emergencyContact.map((contact, index) =>
          <div>
            <h3>Emergency Contact {index + 1}</h3>
            <input type = 'text' placeholder='First Name, Last Name' value = {contact.name} onChange={(e) => {this.setState(prevState => {let s = Object.assign({}, prevState); s.emergencyContact[index].name = e.target.value; return {s};})}}/><br/>
            <input type = 'text' placeholder='phone' value = {contact.phone} onChange={(e) => {this.setState(prevState => {let s = Object.assign({}, prevState); s.emergencyContact[index].phone = e.target.value; return {s};})}}/><br/>
          </div>
          )}
        </div>
        <button onClick={this.saveUsers}>Save</button>
        <p>{this.state.errorMessage}</p>
      </div>
    )
  }
}
